import React, { useEffect, useState } from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { withRouter, Link } from "react-router-dom";
import Box from "@material-ui/core/Box";
import axios from "axios";
import { Form, FormGroup } from "reactstrap";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@rmwc/button";
import { CircularProgress } from "@rmwc/circular-progress";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormControl from "@material-ui/core/FormControl";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import clsx from "clsx";
import IconButton from "@material-ui/core/IconButton";
import "date-fns";
import Collapse from "@material-ui/core/Collapse";
import "@rmwc/typography/styles";
import Titulo from "../Props/Titulo";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import CommentIcon from '@material-ui/icons/Comment';


const serversAddr = require("../serversAddr");
const shajs = require('sha.js');

const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      flexWrap: "wrap",
    },
    colorCard: {
      backgroundColor: "#F7F7EF",
      borderColor: "#ffffff",
    },
    colorCampo: {
      backgroundColor: "#FFFFFF",
      width: "35ch",
    },
    margin: {
      margin: theme.spacing(1),
      width: "100%",
      justifyContent: "center",
    },
    withoutLabel: {
      marginTop: theme.spacing(3),
    },
    textField: {
      width: "75ch",
      backgroundColor: "#FFFFFF",
    },
    tamAlert: {
      width: "200px",
    },
    centrarContenido: {
      textAlign: "center",
    },
    registrarse: {
      textAlign: "right",
      fontSize: 12,
    },
    root2: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
    },
  }));

function CreateAlbum(){
    const classes = useStyles();
    const [valuesAlbum, setValues] = useState({
      "nombre":"",
      "nickname":"",
      "lista": [],
    });

    const [canciones, setCanciones] = useState(
      [
        {"idcancion":-1,"nombre":"name","extension":"mp3","FechaUp":"01/01/2020","Link":"..."},
      ]
    );

    const handleChange = (prop) => (event) => {
      setValues({ ...valuesAlbum, [prop]: event.target.value });
    };

    const [checked, setChecked] = React.useState([0]);

    const handleToggle = (value) => () => {
      const currentIndex = checked.indexOf(value);
      const newChecked = [...checked];

      if (currentIndex === -1) {
        newChecked.push(value);
      } else {
        newChecked.splice(currentIndex, 1);
      }
      setChecked(newChecked);
    };
    
    const submitData = async (event) => {
      event.preventDefault();
      if (valuesAlbum.nombre === ""){
        toast.error("El campo Nombre del album es obligatorio", {
          position: toast.POSITION.TOP_RIGHT,
          autoClose: 5000,
        });
      }else{
        setValues({ ...valuesAlbum, "lista": checked });
        valuesAlbum.lista=checked;
        console.log(valuesAlbum);
        event.preventDefault();
      axios
        .post(
          "http://" +
          serversAddr.backend.host +
          ":" +
          serversAddr.backend.port +
          "/middleware",
          {
            peticion: "/createAlbum",
            server: 4,
            data:
              valuesAlbum
          }
        )
        .then((response) => {
          console.log(response);
        });
      }
    }

    const updateData = async (event) => {
      event.preventDefault();
      axios
        .post(
            "http://" +
            serversAddr.backend.host +
            ":" +
            serversAddr.backend.port +
            "/middleware",
            {
            peticion: "/propscanciones",
            server: 2,
            data: {
                idcancion: 3,
                nombre: 'Fuba',
                extension: '.mp3',
                fechaup: '2021-11-12T00:00:00.000Z',
                link: 'http://facebook.com'
            }
            }
        )
        .then((response) => {
            console.log(response)
            setCanciones(response.data);
        });
      
    };

    useEffect(() => {
      console.log('MOUNTED');
      //Actualizar los datos de localstorage
      valuesAlbum.nickname=localStorage.getItem("username"); //;--Debe ser de localstorage
      return () => {
        //---AQUI EL CODIGO QUE SE EJECUTA CUANDO SE DESMONTE EL COMPONENTE
        console.log('UNMOUNTED');
    };
    },[canciones])
    
    
    return (
        <div>
            <Titulo nombre="Crear Album" etiqueta="register-title" />
            <Box display="flex" justifyContent="center" m={1} p={1} bgcolor="#FFFFFF">
                <Card variant="outlined">
                    <CardContent className={classes.colorCard}>
                        <div align="center">
                            <img
                                align="center"
                                alt="imagen crear album"
                                src="/music.png"
                            />
                        </div>
                        <Box
                            display="flex"
                            justifyContent="center"
                            p={1}
                            flexGrow={1}
                            bgcolor="#F7F7EF"
                        >
                            <Form>
                                <FormGroup>
                                    <Box>
                                        <TextField
                                        label="Nombre del album"
                                        onChange={handleChange("nombre")}
                                        required
                                        id="outlined-required"
                                        className={clsx(classes.margin, classes.textField)}
                                        value={valuesAlbum.nombre}
                                        variant="outlined"
                                        />
                                    </Box>
                                    <div style={{
                                      display: "flex",
                                      justifyContent: "center",
                                      alignItems: "center",
                                    }}>
                                      <List className={classes.root2}>
                                      {canciones.map((value,index) => {
                                        const labelId = `checkbox-list-label-${value}`;

                                        return (
                                          <ListItem values={value} key={index} role={undefined} dense button onClick={handleToggle(value)}>
                                            <ListItemIcon>
                                              <Checkbox
                                                edge="start"
                                                checked={checked.indexOf(value) !== -1}
                                                tabIndex={-1}
                                                disableRipple
                                                inputProps={{ 'aria-labelledby': labelId }}
                                              />
                                            </ListItemIcon>
                                            <ListItemText id={labelId} primary={`${value.idcancion} - ${value.nombre}.${value.extension}`} />
                                          </ListItem>
                                        );
                                      })}
                                    </List>
                                    </div>
                                    <div className={classes.centrarContenido}>
                                      <br/>
                                      <Button
                                        style={{ fontWeight: "bold" }}
                                        label="Actualizar"
                                        onClick={updateData}    
                                      />                                      
                                      <Button
                                        style={{ fontWeight: "bold" }}
                                        label="Crear album"
                                        onClick={submitData}    
                                      />
                                    </div>
                                </FormGroup>
                            </Form>
                        </Box>
                    </CardContent>
                </Card>
            </Box>
        </div>
    )
}

export default CreateAlbum;
