import React, { useEffect, useState } from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Box from "@material-ui/core/Box";
import axios from "axios";
import { Form, FormGroup } from "reactstrap";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@rmwc/button";
import "date-fns";
import "@rmwc/typography/styles";
import Titulo from "../Props/Titulo";
import "react-toastify/dist/ReactToastify.css";

//Import table
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const serversAddr = require("../serversAddr");
const shajs = require('sha.js');

const useStyles = makeStyles((theme) => ({
    root: {
      display: "flex",
      flexWrap: "wrap",
    },
    colorCard: {
      backgroundColor: "#F7F7EF",
      borderColor: "#ffffff",
    },
    colorCampo: {
      backgroundColor: "#FFFFFF",
      width: "35ch",
    },
    margin: {
      margin: theme.spacing(1),
      width: "100%",
      justifyContent: "center",
    },
    withoutLabel: {
      marginTop: theme.spacing(3),
    },
    textField: {
      width: "75ch",
      backgroundColor: "#FFFFFF",
    },
    tamAlert: {
      width: "200px",
    },
    centrarContenido: {
      textAlign: "center",
    },
    registrarse: {
      textAlign: "right",
      fontSize: 12,
    },
    root2: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
    },
    table: {
        minWidth: 650,
      },
  }));


//Función mera
function Propiedades(){
    const classes = useStyles();

    const handleChange = (prop) => (event) => {
      setVal({ ...valuesAlbum, [prop]: event.target.value });
    };

    const [album, setVal]= useState([{
        idcancion: 3,
        nombre: 'Fuba',
        extension: '.mp3',
        fechaup: '2021-11-12T00:00:00.000Z',
        link: 'http://facebook.com'
      }]);

    const [valuesAlbum, setVal2]=useState({
        "idcancion":0,
        "nombre":"",
        "extension":"",
        "fechaup":"",
        "lik":"",
      });

    const [checked, setChecked] = React.useState([0]);

    const handleToggle = (value) => () => {
      const currentIndex = checked.indexOf(value);
      const newChecked = [...checked];

      if (currentIndex === -1) {
        newChecked.push(value);
      } else {
        newChecked.splice(currentIndex, 1);
      }

      setChecked(newChecked);
    };

    //Arreglo inicial
    const [canciones, setCanciones] = useState(
        [
          {"idcancion":-1,"nombre":"name","extension":"mp3","fechaup":"01/01/2020","link":"..."},
        ]
      );
    
    //Método que hace un get al middleware
    const submitData = async (event) => {
      event.preventDefault();

      console.log("Inicio consulta");
      axios
        .post(
            "http://" +
            serversAddr.backend.host +
            ":" +
            serversAddr.backend.port +
            "/middleware",
            {
            peticion: "/propscanciones",
            server: 2,
            data: {
                idcancion: 3,
                nombre: 'Fuba',
                extension: '.mp3',
                fechaup: '2021-11-12T00:00:00.000Z',
                link: 'http://facebook.com'
            }
            }
        )
        .then((response) => {
            console.log(response)
            setCanciones(response.data);
        });
      
    }

    const updateData = async (event) => {
      event.preventDefault();
      console.log(valuesAlbum);
    };

    useEffect(() => {
      console.log('MOUNTED');

      //Actualizar los datos de localstorage
      valuesAlbum.nickname=localStorage.getItem("username"); //;--Debe ser de localstorage
      console.log(valuesAlbum);
      return () => {
        //---AQUI EL CODIGO QUE SE EJECUTA CUANDO SE DESMONTE EL COMPONENTE
        console.log('UNMOUNTED');
    };
    },[canciones])
    
    
    return (
        <div>
            {/* Título del componente e imagen */}
            <Titulo nombre="Propiedades canciones" etiqueta="register-title" />
            <Box display="flex" justifyContent="center" m={1} p={1} bgcolor="#FFFFFF">
                <Card variant="outlined">
                    <CardContent className={classes.colorCard}>
                        <div align="center">
                            <img
                                align="center"
                                alt="imagen crear album"
                                src="/musicatr.png"
                            />
                        </div>
                        <Box
                            display="flex"
                            justifyContent="center"
                            p={1}
                            flexGrow={1}
                            bgcolor="#F7F7EF"
                        >
                            <Form>
                                <FormGroup>
                                    <Box>
                                    {/* <Autocomplete
                                        options={album}
                                        //value={Nalbum.name}
                                        onChange={(event, value) => setVal2({name:value})}
                                        //style={{ width: 300 }}
                                        renderInput={(params) =>
                                        <TextField {...params.nombre} label="Albumes" variant="outlined" />}
                                    /> */}
                                    </Box>
                                    <div style={{
                                      display: "flex",
                                      justifyContent: "center",
                                      alignItems: "center",
                                    }}>
                                      {/* <List className={classes.root2}>
                                      {canciones.map((value,index) => {
                                        const labelId = `checkbox-list-label-${value}`;

                                        return (
                                          <ListItem values={value} key={index} role={undefined} dense button onClick={handleToggle(value)}>
                                            <ListItemIcon>
                                              <Checkbox
                                                edge="start"
                                                checked={checked.indexOf(value) !== -1}
                                                tabIndex={-1}
                                                disableRipple
                                                inputProps={{ 'aria-labelledby': labelId }}
                                              />
                                            </ListItemIcon>
                                            <ListItemText id={labelId} primary={`${value.idcancion} - ${value.nombre}.${value.extension}`} />
                                          </ListItem>
                                        );
                                      })}
                                    </List> */}
                                    </div>
                                    <div className={classes.centrarContenido}>
                                      <br/>
                                      {/* <Button
                                        style={{ fontWeight: "bold" }}
                                        label="Actualizar"
                                        onClick={updateData}    
                                      />                                       */}
                                      <Button
                                        style={{ fontWeight: "bold" }}
                                        label="Mostrar canciones"
                                        onClick={submitData}    
                                      />
                                    </div>
                                    {/* Tabla de resultados */}
                                    <TableContainer component={Paper}>
                                    <Table className={classes.table} aria-label="simple table">
                                        <TableHead>
                                        <TableRow>
                                            <TableCell>Nombre</TableCell>
                                            <TableCell align="right">ID Canción</TableCell>
                                            <TableCell align="right">Extensión</TableCell>
                                            <TableCell align="right">Fecha subida</TableCell>
                                            <TableCell align="right">Link</TableCell>
                                        </TableRow>
                                        </TableHead>
                                        <TableBody>
                                        {canciones.map((valor) => (
                                            <TableRow key={valor.nombre}>
                                            <TableCell component="th" scope="row">{valor.nombre} </TableCell>
                                            <TableCell align="right">{valor.idcancion}</TableCell>
                                            <TableCell align="right">{valor.extension}</TableCell>
                                            <TableCell align="right">{valor.fechaup}</TableCell>
                                            <TableCell align="right">{valor.link}</TableCell>
                                            </TableRow>
                                        ))}
                                        </TableBody>
                                    </Table>
                                    </TableContainer>
                                </FormGroup>
                            </Form>
                        </Box>
                    </CardContent>
                </Card>
            </Box>
        </div>
    )
}

export default Propiedades;
