import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import Links from '../Props/Links';
import { Label } from 'reactstrap';
import Register from '../Register/Register';
import Box from '@material-ui/core/Box';
import { FormGroup } from '@material-ui/core';
import { List } from "@rmwc/list";
import Login from '../Login/Login';
import { ToastContainer } from 'react-toastify';
import User from  '../User/User';
import Cancion from '../Cancion/Cancion'
import Eliminar from  '../Eliminar/Eliminar'
import EliminarC from  '../EliminarC/EliminarC'
import Mover from  '../Mover/Mover'
import Propiedades from '../Propiedades/Propiedades';

import CreateAlbum from '../Album/CreateAlbum'
import Album from '../Album/Album'

import 'bootstrap/dist/css/bootstrap.min.css';
import '@rmwc/typography/styles';
import '@rmwc/list/styles';
import '@material/list/dist/mdc.list.css';
import '@rmwc/drawer/styles';
import '@material/drawer/dist/mdc.drawer.css';
import '@material/ripple/dist/mdc.ripple.css';
import '@rmwc/top-app-bar/styles'
import '@material/top-app-bar/dist/mdc.top-app-bar.css';
import '@material/icon-button/dist/mdc.icon-button.css';
import '@rmwc/icon/icon.css';
import '@rmwc/theme/styles';
import '@material/theme/dist/mdc.theme.css';
import '@rmwc/theme/theme.css';

import {
    TopAppBar,
    TopAppBarRow,
    TopAppBarSection,
    TopAppBarNavigationIcon,
    TopAppBarActionItem,
    TopAppBarTitle,
    TopAppBarFixedAdjust
} from "@rmwc/top-app-bar";

import {
    Drawer,
    DrawerContent,
    DrawerAppContent
} from "@rmwc/drawer";

import {
    ThemeProvider
} from "@rmwc/theme";
import TwoFA from '../2FA/TwoFA';

const useStyles = makeStyles(() => ({
    margen: {
        padding: '4px',
        wordWrap: 'normal',
        display: 'inline-block',
        color: '#454545'
    }

}));

const NavBar = ({ history }) => {
    const classes = useStyles();
    const [open, setOpen] = useState(false);
    const [BanderaRedirigirLogin, setBanderaRedirigirLogin] = useState(false);
    let [local, setLocal] = useState();

    const drawerOpen = () => {
        setOpen(true)
    }

    const comparaRoles = (rol) => {
        if (localStorage.length === 0) {
            return;
        }
        let bandera = false;
        try {
            JSON.parse(localStorage.getItem('roles')).forEach(element => {
                if (element === rol) {
                    bandera = true;
                }
            });
        } catch (e) {
            localStorage.setItem('roles', []);
            console.log(e);
        }
        return bandera;
    }

    useEffect(() => {
        const timeOut = setInterval(() => {
            setLocal(localStorage.getItem('esAutenticado'));
            console.log(local);
        }, 2000);
        return () => clearInterval(timeOut)
    },)

    const salir = () => {
        localStorage.setItem('esAutenticado', false)
        setTimeout(() => {
            setBanderaRedirigirLogin(true)
        }, 500);
    }

    return (
        <ThemeProvider
            options={{
                primary: '#5C8E04',
                secondary: 'black',
                onPrimary: '#000',
                textPrimaryOnBackground: 'black'
            }}
        >
            <ToastContainer />

            <Router history={history}>
                {localStorage.getItem('esAutenticado') === 'true' ?
                    <Drawer modal open={open} onClose={() => setOpen(false)}>
                        <br />
                        <DrawerContent>
                            <List>
                                {/* ROL DE ADMINISTRADOR*/}
                                {(comparaRoles('usuario')) ?
                                    <FormGroup>
                                        <Box> <Label style={{ fontWeight: 'bold', color: '#CA5300' }} className={classes.margen}>- Opciones</Label></Box>
                                        <Box><Links path='/' texto='Inicio' /></Box>
                                        <Box><Links path='/cancion' texto='Cancion' /></Box>    
                                        <Box><Links path='/Propiedades' texto='Propiedades canciones' /></Box>                                         
                                        <Box><Links path='/createAlbum' texto='Crear album' /></Box>
                                        <Box><Links path='/myAlbums' texto='Ver album' /></Box>
                                        <Box><Links path='/Eliminar' texto='Eliminar/Modificar Album' /></Box>  
                                        <Box><Links path='/EliminarC' texto='Eliminar/Modificar Cancion' /></Box>
                                        <Box><Links path='/MoverC' texto='Mover Cancion' /></Box>                                       
                                    </FormGroup>
                                    : null}
                            </List>
                        </DrawerContent>
                    </Drawer>
                    : null}

                <DrawerAppContent>
                    {localStorage.getItem('esAutenticado') === 'true' ?
                        <TopAppBar fixed short>
                            <TopAppBarRow  >
                                <TopAppBarSection>
                                    <TopAppBarNavigationIcon icon="menu" onClick={() => drawerOpen(!open)} />
                                    <TopAppBarTitle>Sistema</TopAppBarTitle>
                                </TopAppBarSection>
                                <TopAppBarSection alignEnd>
                                    <TopAppBarActionItem icon={{
                                        strategy: 'component',
                                        icon: 'logout'
                                    }}
                                        onClick={(e) => salir(e)} />
                                </TopAppBarSection>
                            </TopAppBarRow>
                        </TopAppBar>
                        : null}
                    <TopAppBarFixedAdjust />

                    <div style={{ height: '100rem' }}>

                        <Switch>
                            {/* ROL DE USUARIO */}
                            {(localStorage.getItem('esAutenticado') === 'false') ? <Route exact path='/' component={Login} /> : null}
                            {(localStorage.getItem('esAutenticado') === 'false') ? <Route exact path='/registrar' component={Register} /> : null}
                            {(localStorage.getItem('esAutenticado') === 'false') ? <Route exact path='/autenticar' component={TwoFA} /> : null}

                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/' component={User} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/Perfil' component={User} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/Cancion' component={Cancion} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/Eliminar' component={Eliminar} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/EliminarC' component={EliminarC} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/MoverC' component={Mover} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/Propiedades' component={Propiedades} ></Route> : null}

                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/createAlbum' component={CreateAlbum} ></Route> : null}
                            {(localStorage.getItem('esAutenticado') === 'true') ? <Route exact path='/myAlbums' component={Album} ></Route> : null}

                            {BanderaRedirigirLogin ? <Redirect to='/' /> : null}

                            <Redirect from="*" to="/" />

                        </Switch>
                    </div>
                </DrawerAppContent>
            </Router>
        </ThemeProvider>
    )
}

export default withRouter(NavBar);
