import React, { useEffect, useState } from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { withRouter, Link } from "react-router-dom";
import Box from "@material-ui/core/Box";
import axios from "axios";
import { Form, FormGroup } from "reactstrap";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import { Button } from "@rmwc/button";
import { CircularProgress } from "@rmwc/circular-progress";
import OutlinedInput from "@material-ui/core/OutlinedInput";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormControl from "@material-ui/core/FormControl";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import clsx from "clsx";
import IconButton from "@material-ui/core/IconButton";
import "date-fns";
import Collapse from "@material-ui/core/Collapse";
import VpnKeyIcon from "@material-ui/icons/VpnKey";
import "@rmwc/typography/styles";
import Titulo from "../Props/Titulo";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const shajs = require('sha.js');
const serversAddr = require("../serversAddr");

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
  },
  colorCard: {
    backgroundColor: "#F7F7EF",
    borderColor: "#ffffff",
  },
  margin: {
    margin: theme.spacing(1),
    width: "100%",
    justifyContent: "center",
  },
  textField: {
    width: "35ch",
    backgroundColor: "#FFFFFF",
  },
  centrarContenido: {
    textAlign: "center",
  },
  registrarse: {
    textAlign: "right",
    fontSize: 12,
  },
}));

const Login = ({ history }) => {
  const [activarBotonGuardar, setActivarBotonGuardar] = useState(true);

  const classes = useStyles();
  const [valuesUser, setValues] = useState({
    username: "",
    password: "",
    showPassword: false,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...valuesUser, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...valuesUser, showPassword: !valuesUser.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  useEffect(() => {}, []);

  const handleClick = async (event) => {
    event.preventDefault();
    let correcto = true;
    if (valuesUser.username === "") {
      toast.error("El campo Nombre de Usuario es obligatorio", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 5000,
      });
      correcto = false;
    }
    if (valuesUser.password === "") {
      toast.error("El campo Contrase침a es obligatorio", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 5000,
      });
      correcto = false;
    }
    if (valuesUser.username === "") {
      toast.error("Es obligatorio el nombre de usuario", {
        position: toast.POSITION.TOP_RIGHT,
        autoClose: 5000,
      });
      correcto = false;
    }
    if (correcto) {
      let passEncrypt = shajs("sha256")
        .update(valuesUser.password)
        .digest("hex");
      //Verificacion de Credenciales
      await axios
        .post(
          "http://" +
            serversAddr.backend.host +
            ":" +
            serversAddr.backend.port +
            "/middleware",
          {
            peticion: "/login",
            server: 1,
            data: {
              nickname: valuesUser.username, 
              password: passEncrypt
            }
          }
        )
        .then((response) => {
          if (response.data.exists === 0) {
              toast.error("Credenciales Incorrectas", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 5000,
              });
          } else {
            //existe el usuario
            if(response.data.doblefactor)
            {
              axios
              .post(
                "http://" +
                  serversAddr.backend.host +
                  ":" +
                  serversAddr.backend.port +
                  "/middleware",
                {
                  peticion: "/enviar_token",
                  server: 1,
                  data: {
                    nickname: valuesUser.username
                  }
                }
              )
              .then((response) => {
                if (response.data.status) {
                  toast.success("Revisar su correo electronico por el codigo de verificacion", {
                    position: toast.POSITION.TOP_RIGHT,
                    autoClose: 5000,
                  });
                } else {
                  //existe el usuario
                  toast.error("No se a podido enviar el correo", {
                    position: toast.POSITION.TOP_RIGHT,
                    autoClose: 5000,
                  });
                }
              });
              history.push("/autenticar");
            }
            else
            {
              toast.success("Bienvenido", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 5000,
              });
              localStorage.setItem("esAutenticado", true);
              localStorage.setItem("roles", JSON.stringify(["usuario"]));
              localStorage.setItem("permisos", JSON.stringify(["usuario"]));
              localStorage.setItem("username", valuesUser.username);
              setActivarBotonGuardar(false);
              history.push("/");
            }
          }
        });
    }
  };

  return (
    <div>
      <Titulo nombre="Inicio de Sesi칩n" etiqueta="login-title" />
      <Box display="flex" justifyContent="center" m={1} p={1} bgcolor="#FFFFFF">
        <Card variant="outlined">
          <CardContent className={classes.colorCard}>
            <div align="center">
              <img
                align="center"
                alt="imagen ususario iniciar sesi칩n"
                src="/logIn.png"
              />
            </div>
            <Box
              display="flex"
              justifyContent="center"
              p={1}
              flexGrow={1}
              bgcolor="#F7F7EF"
            >
              <Form onSubmit={handleClick}>
                <FormGroup>
                  <Box display="flex" justifyContent="center">
                    <TextField
                      label="Nombre de Usuario"
                      onChange={handleChange("username")}
                      required
                      id="outlined-required"
                      className={clsx(classes.margin, classes.textField)}
                      value={valuesUser.username}
                      variant="outlined"
                    />
                  </Box>
                  <Box display="flex" justifyContent="center">
                    <FormControl
                      className={clsx(classes.margin, classes.textField)}
                      variant="outlined"
                    >
                      <InputLabel htmlFor="outlined-adornment-password">
                        Password
                      </InputLabel>
                      <OutlinedInput
                        id="outlined-adornment-password"
                        type={valuesUser.showPassword ? "text" : "password"}
                        value={valuesUser.password}
                        onChange={handleChange("password")}
                        endAdornment={
                          <InputAdornment position="end">
                            <IconButton
                              aria-label="toggle password visibility"
                              onClick={handleClickShowPassword}
                              onMouseDown={handleMouseDownPassword}
                              edge="end"
                            >
                              {valuesUser.showPassword ? (
                                <Visibility />
                              ) : (
                                <VisibilityOff />
                              )}
                            </IconButton>
                          </InputAdornment>
                        }
                        labelWidth={70}
                      />
                    </FormControl>
                  </Box>
                  <div className={classes.centrarContenido}>
                    <Collapse in={!activarBotonGuardar}>
                      <Button
                        style={{ fontWeight: "bold" }}
                        label=" ...Entrando"
                        icon={<CircularProgress />}
                      />
                    </Collapse>
                    <Collapse in={activarBotonGuardar}>
                      <Button
                        data-testid="button-login"
                        variant="contained"
                        size="small"
                        style={{
                          color: "#FFFFFF",
                          backgroundColor: "#0B78F4",
                          width: "315px",
                          justifyContent: "center",
                          alignItems: "center",
                        }}
                        onClick={handleClick}
                      >
                        <VpnKeyIcon /> Entrar
                      </Button>
                    </Collapse>
                    <Link to="/registrar" className={classes.registrarse}>
                      No tienes Cuenta? Registrate!
                    </Link>
                  </div>
                </FormGroup>
              </Form>
            </Box>
          </CardContent>
        </Card>
      </Box>
    </div>
  );
};

export default withRouter(Login);
